$(document).ready(function(){
    $('.pro-dec-big-img-slider').slick({
        slidesToShow: 1,
        slidesToScroll: 1,
        arrows: false,
        fade: true,
        asNavFor: '.product-dec-slider'
    });

    $('.product-dec-slider').slick({
        slidesToShow: 3,
        slidesToScroll: 1,
        asNavFor: '.pro-dec-big-img-slider',
        dots: false,
        centerMode: true,
        focusOnSelect: true
    });
});
$('.product-dec-slider').slick({
    slidesToShow: 3,          // số ảnh nhỏ hiển thị cùng lúc
    slidesToScroll: 1,
    asNavFor: '.pro-dec-big-img-slider',
    dots: false,
    centerMode: false,        // tắt center mode để ảnh sát lề trái
    focusOnSelect: true,
    arrows: false,
    infinite: true,
    variableWidth: false,     // giữ cố định kích thước slide
});
$(document).ready(function() {
    $('.product-dec-small img').click(function() {
        var src = $(this).attr('src');
        // Thay đổi ảnh lớn
        $('.pro-dec-big-img-slider').find('a').attr('href', src);
        $('.pro-dec-big-img-slider').find('img').attr('src', src);
        // Nếu bạn dùng plugin EasyZoom, cần cập nhật lại
        if (typeof $.fn.easyZoom !== 'undefined') {
            var ez = $('.easyzoom').data('easyZoom');
            ez.reset();
        }
    });
});

    function changeQuantity(delta) {
        const input = document.getElementById("quantity");
        let value = parseInt(input.value);
        if (isNaN(value)) value = 1;
        value += delta;
        if (value < 1) value = 1;
        input.value = value;
    }


